<?php
require_once __DIR__ . '/../vendor/autoload.php';


use function Mhrlife\PhpaiKit\create_agent;

$sample_image = "data:image/png;base64,";


$openai = \OpenAI::factory()
    ->withApiKey(getenv('LLM_COURSE_OPENROUTER_API_KEY'))
    ->withBaseUri("https://openrouter.ai/api/v1")
    ->make();


$agent = create_agent($openai);

$response = $agent->run([
    ['role' => 'user', 'content' => [
        [
            'type' => 'text',
            'text' => 'Describe the image below in detail.'
        ],
        [
            'type' => 'image_url',
            'image_url' => [
                'url' => $sample_image,
            ]
        ]
    ]],
]);

echo $response . PHP_EOL; # The image is a smooth color gradient transitioning diagonally ...

